/* eslint-env browser */
import css from './module2.css';
import image from './image.png';

export function createParagraph(): HTMLParagraphElement {
	const element: HTMLParagraphElement = document.createElement('p');
	element.className = css.class1;
	element.innerHTML = 'Hello from module2';
	return element;
}

export function createImage(): HTMLImageElement {
	const element: HTMLImageElement = document.createElement('img');
	element.src = image;
	return element;
}
